mapper.select:
SELECT *, f_get_ark_code_table_name ( 'common_status', STATUS ) AS status_name, f_get_user_name ( creator_id ) AS creator_name, f_get_user_name ( updator_id ) AS updator_name FROM st_system_config_category WHERE is_deleted = 0
and create_time >= #{queryDTO.startCreateDate}
and create_time <= #{queryDTO.endCreateDate}
and `name` like concat('%',concat(#{queryDTO.name},'%'))
and identifier like concat('%',concat(#{queryDTO.identifier},'%'))
and status = #{queryDTO.status}
order by create_time desc
